import styled from 'styled-components';

export const Container = styled.div`
  width: 70%;
  height: 80vh;
  background-color: #F8F8F8;
  border-radius: 6px;
  box-shadow: 0 4px 4px 0 rgb(0 0 0 / 50%);
`;

export const Header = styled.div`
  width: 100%;
  height: 150px;
  background-color: #9970FB;
  border-top-left-radius: 6px;
  border-top-right-radius: 6px;
`

export const Title = styled.h1`
  text-align: center;
  padding: 30px 0;
  color: #fff;
`
export const OpenCreateTodo = styled.button`
  border: none;
  position: fixed;
  width: 50px;
  height: 50px;
  border-radius: 50%;
  background-color: #976EF8;
  color: #fff;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  font-size: 20px;
  top: 80%;
  left: 48.5%;
  transition: ease 0.2s;

  &:hover {
    background-color: #8a5df5;
  }
`

export const TodoItem = styled.div`

`
