import styled from 'styled-components';

export const Container = styled.div`
  width: 70%;
  height: 80vh;
  background-color: #292929;
  border-radius: 6px;
`;

export const CloseCreateTodo = styled.button`
  border: none;
  position: fixed;
  width: 50px;
  height: 50px;
  border-radius: 50%;
  background-color: #1A1919;
  color: #FBCD68;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  font-size: 20px;
  top: 75%;
  left: 48.5%;
  transition: ease 0.2s;

  &:hover {
    background-color: #141414;
  }
`

export const Form = styled.form`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center; 
`

export const FormItem = styled.div`
  margin: 0 0 20px 0;
  display: block;
  width: 80%;
`

export const FormInput = styled.input`
  padding: 20px 30px;
  border: none;
  border-radius: 6px;
  background-color: #2e2933;
  color: #E3E3E4;
  width: 100%;

  &:focus {
    outline: none;
    border: solid 1px #393340; 
  }
`

export const FormBtn = styled.button`
  display: block;  
  width: 80%;
  padding: 15px 0;
  cursor: pointer;
  background-color: #9734FC;
  color: #e8e8e8;
  border: none;
  border-radius: 6px;
  font-size: 16px;
  transition: ease 0.2s;

  &:focus {
    outline: none;
  }

  &:hover {
    background-color: #8b11f2;   
  }
`
