import styled from 'styled-components';

export const TaskContainer = styled.div`
  padding: 10px 15px;
  display: flex;
`

export const DelBtn = styled.button`
  border: none;
  outline: none;
  background-color: red;
  color: #fff;
  width: 25px;
  height: 25px;
  border-radius: 50%;
  margin: 0 8px 0 0;
`

export const CheckBtn = styled.button`
  border: none;
  outline: none;
  background-color: green;
  color: #fff;
  width: 25px;
  height: 25px;
  border-radius: 50%;
  margin: 0 8px 0 0;
  justify-self
`
