import React, { useState } from 'react';

import { Link } from 'react-router-dom';

import { Container, CloseCreateTodo, Form, FormItem, FormInput, FormBtn } from './styles';

type Props = { 
  saveTodo: (e: React.FormEvent, formData: TodoInterface | any) => void 
}

const CreateTodo: React.FC<Props> = ({ saveTodo }) => {
  const [formData, setFormData] = useState<TodoInterface | {}>()

  const handleForm = (e: React.FormEvent<HTMLInputElement>): void => {
    setFormData({
      ...formData,
      [e.currentTarget.id]: e.currentTarget.value,
    })
  }

  return (
    <>
      <Container>

          <Form>
            <FormItem>
              <FormInput onChange={handleForm} type='text' id='name' placeholder='Digite sua tarefa'></FormInput>
            </FormItem>
            <FormItem>
              <FormInput onChange={handleForm} type='text' id='description'></FormInput>
            </FormItem>
            <FormBtn>Adicionar Tarefa</FormBtn>
          </Form>
          <Link to ="/">
            <CloseCreateTodo>
              x
            </CloseCreateTodo>
        </Link>
      </Container>
    </>
  )
}

export default CreateTodo;
