import React from 'react';

import { TaskContainer, DelBtn } from './TodoItemStyle';

type Props = TodoProps & {
    updateTodo: (todo: TodoInterface) => void
    deleteTodo: (_id: string) => void
}

const TodoItem: React.FC<Props> = ({ todo, updateTodo, deleteTodo }) => {
  return (
    <TaskContainer>
      <div className='Card--text'>
        <DelBtn onClick={() => deleteTodo(todo._id)}>X</DelBtn>
        <span>{todo.description}</span>
      </div>
      <div className='Card--button'>
        <button onClick={() => updateTodo(todo)}>Tarefa Completa</button>

      </div>
    </TaskContainer>
  )
}

export default TodoItem
