import React, { useState } from 'react';

import { Link } from 'react-router-dom';

import { Container, CloseCreateTodo, Form, FormItem, FormInput, FormLabel, FormBtn } from './styles';

type Props = { 
  saveTodo: (e: React.FormEvent, formData: TodoInterface | any) => void 
}

const CreateTodo: React.FC<Props> = ({ saveTodo }) => {
  const [formData, setFormData] = useState<TodoInterface | {}>()

  const handleForm = (e: React.FormEvent<HTMLInputElement>): void => {
    setFormData({
      ...formData,
      [e.currentTarget.id]: e.currentTarget.value,
    })
  }

  return (
    <>
      <Container>
        <Link to ="/">
          <Form>
            <FormItem>
              <FormLabel>Digite a Tarefa</FormLabel>
              <FormInput onChange={handleForm} type='text' id='name'></FormInput>
            </FormItem>
            <FormItem>
              <FormLabel>Digite a descrição da Tarefa</FormLabel>
              <FormInput onChange={handleForm} type='text' id='description'></FormInput>
            </FormItem>
          </Form>
          <CloseCreateTodo>
            x
          </CloseCreateTodo>
        </Link>
      </Container>
    </>
  )
}

export default CreateTodo;
